import React, { useState } from 'react';
import { Navbar, Nav, Form, FormControl } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import logo from '../assets/logo.png'; // Importa l'immagine dalla cartella assets

function CustomNavbar() {
  const [searchVisible, setSearchVisible] = useState(false);

  return (
    <Navbar bg="dark" variant="dark" expand="lg">
      <Navbar.Brand href="#home">
        <img
          src={logo} // Usa l'immagine importata
          width="100"
          height="60"
          className="d-inline-block align-top"
          alt="Brand Logo"
        />
      </Navbar.Brand>
      <Navbar.Toggle aria-controls="basic-navbar-nav" />
      <Navbar.Collapse id="basic-navbar-nav">
        <Nav className="me-auto"> {/* 'me-auto' spinge il contenuto successivo a destra */}
          <Nav.Link href="#link1" className="text-white">Link1</Nav.Link>
          <Nav.Link href="#link2" className="text-white">Link2</Nav.Link>
          <Nav.Link href="#link3" className="text-white">Link3</Nav.Link>
          <Nav.Link href="#link4" className="text-white">Link4</Nav.Link>
          <Nav.Link href="#link5" className="text-white">Link5</Nav.Link>
        </Nav>
        <Form className="d-flex ms-auto"> {/* 'ms-auto' per spingere questo form all'estrema destra */}
          {searchVisible && (
            <FormControl type="text" placeholder="Search" className="mr-2" />
          )}
          {/* Pulsante senza bordo con padding e ingrandimento dell'icona */}
          <button
            onClick={() => setSearchVisible(!searchVisible)}
            style={{
              background: 'none',
              border: 'none',
              color: 'white',
              cursor: 'pointer',
              padding: '0 10px', /* Aggiungi padding laterale */
              outline: 'none',
              fontSize: '1.5rem', /* Aumenta la dimensione dell'icona */
            }}
          >
            <i className="bi bi-search"></i>
          </button>
        </Form>
      </Navbar.Collapse>
    </Navbar>
  );
}

export default CustomNavbar;
