import React, { useEffect, useState } from 'react';
import { Carousel, Container } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

const API_KEY = '6953698b';

// Lista di film da escludere
const EXCLUDED_MOVIES = [
  'tt0073629',
  'tt14993250',
  'tt0131704',
  'tt3410408'
];

function FilmList({ title, query }) {
  const [movies, setMovies] = useState([]);

  useEffect(() => {
    fetch(`http://www.omdbapi.com/?apikey=${API_KEY}&s=${query}&type=movie`)
      .then(response => response.json())
      .then(data => {
        // Filtra i film da escludere
        const filteredMovies = data.Search.filter(movie => !EXCLUDED_MOVIES.includes(movie.imdbID));
        setMovies(filteredMovies);
      });
  }, [query]);

  return (
    <Container>
      <h2 style={{ color: 'white' }}>{title}</h2>
      <Carousel
        controls={true}
        indicators={false}
        slide={true}
        interval={null}
        style={{ overflow: 'hidden' }}
        nextLabel=""  // Rimuove il testo del pulsante
        prevLabel=""  // Rimuove il testo del pulsante
      >
        {movies.map((movie, index) => (
          <Carousel.Item key={movie.imdbID}>
            <img
              src={movie.Poster}
              alt={movie.Title}
              style={{ width: '100%', height: 'auto', borderRadius: '5px' }}
            />
          </Carousel.Item>
        ))}
      </Carousel>
    </Container>
  );
}

export default FilmList;
