import React, { useEffect, useState } from 'react';
import Slider from 'react-slick';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import './FilmList.css'; // Importa il file CSS personalizzato

const API_KEY = '6953698b';

// Lista di film da escludere
const EXCLUDED_MOVIES = [
  'tt0073629',
  'tt14993250',
  'tt0131704',
  'tt3410408'
];

function FilmList({ title, query }) {
  const [movies, setMovies] = useState([]);

  useEffect(() => {
    fetch(`http://www.omdbapi.com/?apikey=${API_KEY}&s=${query}&type=movie`)
      .then(response => response.json())
      .then(data => {
        // Filtra i film da escludere
        const filteredMovies = data.Search.filter(movie => !EXCLUDED_MOVIES.includes(movie.imdbID));
        setMovies(filteredMovies);
      });
  }, [query]);

  // Impostazioni per react-slick
  const settings = {
    dots: false,
    infinite: true,
    speed: 500,
    slidesToShow: 5, // Mostra 5 film per volta
    slidesToScroll: 1, // Scorri di 1 film alla volta
    arrows: true, // Mostra le frecce per lo scorrimento
    centerMode: true, // Centra gli elementi
    centerPadding: '0', // Nessun padding ai lati
    responsive: [
      {
        breakpoint: 1024,
        settings: {
          slidesToShow: 4,
          slidesToScroll: 1,
          infinite: true,
          dots: false,
          centerPadding: '0'
        }
      },
      {
        breakpoint: 768,
        settings: {
          slidesToShow: 3,
          slidesToScroll: 1,
          infinite: true,
          dots: false,
          centerPadding: '0'
        }
      },
      {
        breakpoint: 480,
        settings: {
          slidesToShow: 2,
          slidesToScroll: 1,
          infinite: true,
          dots: false,
          centerPadding: '0'
        }
      }
    ]
  };

  return (
    <div style={{ padding: '20px', backgroundColor: '#212121' }}>
      <h2 style={{ color: 'white' }}>{title}</h2>
      <Slider {...settings}>
        {movies.map((movie) => (
          <div key={movie.imdbID} style={{ display: 'flex', justifyContent: 'center', padding: '0 10px' }}>
            <img
              src={movie.Poster}
              alt={movie.Title}
              style={{ width: '200px', height: '300px', objectFit: 'contain', borderRadius: '5px' }}
            />
          </div>
        ))}
      </Slider>
    </div>
  );
}

export default FilmList;
