import React, { useEffect, useState } from 'react';
import { Carousel, Container, Row, Col } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

const API_KEY = '6953698b';

function FilmList({ title, query }) {
  const [movies, setMovies] = useState([]);

  useEffect(() => {
    fetch(`http://www.omdbapi.com/?apikey=${API_KEY}&s=${query}`)
      .then(response => response.json())
      .then(data => setMovies(data.Search || []));
  }, [query]);

  // Helper function to split the movies array into chunks of 5
  const chunkArray = (array, size) => {
    const result = [];
    for (let i = 0; i < array.length; i += size) {
      result.push(array.slice(i, i + size));
    }
    return result;
  };

  // Divide the movies into chunks of 5 per row
  const rows = chunkArray(movies, 5);

  return (
    <Container>
      <h2 style={{ color: 'white' }}>{title}</h2>
      <Carousel controls={true} indicators={false} slide={true} interval={null} style={{ overflow: 'hidden' }}>
        {rows.map((row, rowIndex) => (
          <Carousel.Item key={rowIndex}>
            <Row className="d-flex flex-nowrap">
              {row.map((movie) => (
                <Col key={movie.imdbID} xs={2} className="p-2">
                  <img
                    src={movie.Poster}
                    alt={movie.Title}
                    style={{ width: '100%', height: 'auto', borderRadius: '5px' }}
                  />
                </Col>
              ))}
            </Row>
          </Carousel.Item>
        ))}
      </Carousel>
    </Container>
  );
}

export default FilmList;
