import React, { useState } from 'react';
import { Navbar, Nav, Form, FormControl, Button } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap-icons/font/bootstrap-icons.css'; // Importa le icone Bootstrap
import logo from '../assets/logo.png'; // Importa l'immagine dalla cartella assets
import './Navbar.css'; // Importa il file CSS per le animazioni

function CustomNavbar({ onSearch }) {
  const [searchVisible, setSearchVisible] = useState(false);
  const [query, setQuery] = useState('');

  const handleSearch = () => {
    if (query) {
      fetch(`http://www.omdbapi.com/?apikey=6953698b&s=${query}&type=movie`)
        .then(response => response.json())
        .then(data => {
          const filteredMovies = data.Search || [];
          onSearch(query, filteredMovies); // Passa query e risultati al componente App
        });
    }
    setSearchVisible(false); // Chiude la barra di ricerca dopo la ricerca
  };

  return (
    <Navbar bg="dark" variant="dark" expand="lg">
      <Navbar.Brand href="#home">
        <img
          src={logo} // Usa l'immagine importata
          width="100"
          height="60"
          className="d-inline-block align-top"
          alt="Brand Logo"
        />
      </Navbar.Brand>
      <Navbar.Toggle aria-controls="basic-navbar-nav" />
      <Navbar.Collapse id="basic-navbar-nav">
        <Nav className="me-auto">
          <Nav.Link href="#link1" className="text-white">Home</Nav.Link>
          <Nav.Link href="#link2" className="text-white">Tv Shows</Nav.Link>
          <Nav.Link href="#link3" className="text-white">Movies</Nav.Link>
          <Nav.Link href="#link4" className="text-white">Recently Added</Nav.Link>
          <Nav.Link href="#link5" className="text-white">My List</Nav.Link>
        </Nav>
        <Form className="search-form ms-auto">
          <FormControl
            type="text"
            placeholder="Search"
            className={`search-bar ${searchVisible ? 'visible' : ''}`}
            value={query}
            onChange={(e) => setQuery(e.target.value)}
            onKeyPress={(e) => {
              if (e.key === 'Enter') {
                handleSearch();
              }
            }}
          />
          <Button
            onClick={handleSearch}
            className="search-button"
          >
            <i className="bi bi-search"></i>
          </Button>
        </Form>
      </Navbar.Collapse>
    </Navbar>
  );
}

export default CustomNavbar;
