import React, { useState } from 'react';
import { Navbar, Nav, Form, FormControl, Button } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import logo from '../assets/logo.png'; // Importa l'immagine dalla cartella assets
import './Navbar.css'; // Assicurati di importare il CSS corretto

function CustomNavbar({ onSearch }) {
  const [searchVisible, setSearchVisible] = useState(false);
  const [query, setQuery] = useState('');

  const handleSearch = () => {
    // Fetch results from the API
    fetch(`http://www.omdbapi.com/?apikey=6953698b&s=${query}&type=movie`)
      .then(response => response.json())
      .then(data => {
        const filteredMovies = data.Search || [];
        onSearch(query, filteredMovies); // Pass query and results
      });
    setSearchVisible(false); // Chiude la barra di ricerca dopo la ricerca
  };

  return (
    <Navbar bg="dark" variant="dark" expand="lg">
      <Navbar.Brand href="#home">
        <img
          src={logo} // Usa l'immagine importata
          width="100"
          height="60"
          className="d-inline-block align-top"
          alt="Brand Logo"
        />
      </Navbar.Brand>
      <Navbar.Toggle aria-controls="basic-navbar-nav" />
      <Navbar.Collapse id="basic-navbar-nav">
        <Nav className="me-auto"> {/* 'me-auto' spinge il contenuto successivo a destra */}
          <Nav.Link href="#link1" className="text-white">Link1</Nav.Link>
          <Nav.Link href="#link2" className="text-white">Link2</Nav.Link>
          <Nav.Link href="#link3" className="text-white">Link3</Nav.Link>
          <Nav.Link href="#link4" className="text-white">Link4</Nav.Link>
          <Nav.Link href="#link5" className="text-white">Link5</Nav.Link>
        </Nav>
        <Form className="d-flex ms-auto search-form"> {/* 'ms-auto' per spingere questo form all'estrema destra */}
          <FormControl
            type="text"
            placeholder="Search"
            className={`search-bar ${searchVisible ? 'visible' : ''}`}
            value={query}
            onChange={(e) => setQuery(e.target.value)}
            onKeyPress={(e) => {
              if (e.key === 'Enter') {
                handleSearch();
              }
            }}
          />
          <Button
            onClick={() => {
              handleSearch();
            }}
            variant="outline-light"
            className="search-button"
          >
            <i className="bi bi-search"></i>
          </Button>
        </Form>
      </Navbar.Collapse>
    </Navbar>
  );
}

export default CustomNavbar;
